import React, { useState } from "react";
import "./Style.css";
const Input=()=> {
    return <input type="text"/>
}
function ToDoList() {
    const [data, setData] = useState();
    const [listItem, setListItem] = useState([])
    const InputData = (event) => {
        setData(event.target.value)
    }
    const listOfItem=() => {
        setListItem(listItem.concat(data));
        // or
        // setListItem((oldItems)=> {
        //     return [...oldItems, data];
        // })
    }
    return (
        <div className="mainBlock">
            <div className="insideBlock">
                <div className="title">ToDo List</div>
                <div className="content">
                    <input type="text" onChange={InputData} placeholder="Add a Item" />   
                <button className="btnAdd" onClick={listOfItem} >+</button>
                </div>
                <ol className="list">
                    {
                        listItem.map((item)=>{
                            return <li>{item}</li>
                        })
                    }
                </ol>
            </div>
        </div>
    )
}
export default ToDoList;
